                                                    data-bs-toggle="collapse"
                                                    data-bs-target="#collapse<?php echo $cat->name . $index; ?>"
                                                    aria-expanded="false"
                                                    aria-controls="collapse<?php echo $cat->name . $index; ?>">
                                                <?php echo get_field('question') ?>
                                            </button>
                                        </h2>
                                        <div id="collapse<?php echo $cat->name . $index; ?>"
                                             class="accordion-collapse collapse"
                                             aria-labelledby="heading<?php echo $cat->name . $index; ?>"
                                             data-bs-parent="#accordionExample">
                                            <div class="accordion-body">
                                                <p><?php echo get_field('answer') ?></p>
                                            </div>
                                        </div>
                                    </div>

                                <?php endwhile;
                                endif; ?>

                            </div>
                        </div>
                    </div>
                </section>
